import { Component, OnInit } from "@angular/core";
import { QuizData } from "src/app/models/models";
import { QuizService } from "src/app/services/quiz.service";
import { Observable } from "rxjs";

@Component({
  selector: "app-quiz",
  templateUrl: "./quiz.component.html",
  styleUrls: ["./quiz.component.scss"],
})
export class QuizComponent implements OnInit {
  quizItems!: QuizData[];
  curIdx$!: Observable<number>;
  correctCount$!: Observable<number | null>;
  checkAnswer$!: Observable<boolean>;
  answerIsChoosed$!: Observable<boolean>;

  constructor(private quizService: QuizService) {}

  ngOnInit(): void {
    this.quizItems = this.quizService.quizData;
    this.curIdx$ = this.quizService.currentIdx$;
    this.correctCount$ = this.quizService.correctCount$;
    this.checkAnswer$ = this.quizService.checkAnswer$;
    this.answerIsChoosed$ = this.quizService.answerIsChoosed$;
  }

  handleChoose(i: number, corAnsw: number, id: string) {
    this.quizService.handleChoose(i, corAnsw, id);
  }

  handleNext() {
    this.quizService.handleNext();
  }
}
<div class="quiz__wrapper">
  <div *ngIf="(curIdx$ | async) !== quizItems.length; else elseBlock">
    <div class="quiz__items" *ngFor="let quiz of quizItems; let i = index">
      <div
        class="quiz__item"
        *ngIf="(curIdx$ | async) === i"
        [ngClass]="{
          true__answer: checkAnswer$ | async,
          false__answer:
            !(checkAnswer$ | async) && (checkAnswer$ | async) !== null,
          choosed__answer: answerIsChoosed$ | async
        }"
      >
        <h2>{{ quiz.question }}</h2>
        <ul
          class="quiz__answers"
          *ngFor="let answer of quiz.answers; let i = index"
        >
          <li
            class="quiz__answer"
            (click)="handleChoose(i + 1, quiz.correctAnswer, answer.id)"
          >
            <p>{{ i + 1 }}.</p>
            <h3 [ngClass]="{ choosed__answer: answer.isChoosed }">{{ answer.answer }}</h3>
          </li>
        </ul>
      </div>
    </div>
    <button
      [disabled]="!(answerIsChoosed$ | async)"
      class="quiz__button"
      (click)="handleNext()"
    >
      Next
    </button>
  </div>
  <ng-template #elseBlock>
    <app-result
      [props]="{ correctCount: correctCount$ | async, quizItems }"
    ></app-result>
  </ng-template>
</div>
