import { Component, OnInit } from "@angular/core";
import { Observable } from "rxjs";
import { QuizService } from "src/app/services/quiz.service";

@Component({
  selector: "app-progress",
  templateUrl: "./progress.component.html",
  styleUrls: ["./progress.component.scss"],
})
export class ProgressComponent implements OnInit {
  curIdx$!: Observable<number>;
  // progressWidth$!: Observable<string | null>;

  constructor(private quizService: QuizService) {}

  ngOnInit(): void {
    this.curIdx$ = this.quizService.currentIdx$;
    // this.progressWidth$ = this.quizService.progressWidth$
    this.quizService.progressWidth$.subscribe((width) => {
      document.documentElement.style.setProperty('--progress-width', `${width}px`);
    });
  }
}
<div class="progress__wrapper">
  <div class="progress"></div>
  <div class="progress__active"></div>
</div>
