import { Injectable, OnInit } from '@angular/core';
import { QuizData } from '../models/models';
import { BehaviorSubject } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class QuizService implements OnInit {
  private currentIdxSubject: BehaviorSubject<number> = new BehaviorSubject<number>(0);
  private correctCountSubject: BehaviorSubject<number> = new BehaviorSubject<number>(0);
  currentIdx$ = this.currentIdxSubject.asObservable();
  correctCount$ = this.correctCountSubject.asObservable()
  progressWidth = '92px'
  quizData: QuizData[] = [
    {
      id: Math.random().toString(),
      question: 'How many oceans are there ?',
      answers: ['Seven', 'Two', 'Four', 'Nine'],
      correctAnswer: 3,
    },
    {
      id: Math.random().toString(),
      question: 'Who discovered America ?',
      answers: ['Mozart', 'Fernan Magellan', 'Vasco Da Gamma', 'Christopher Colombos'],
      correctAnswer: 4,
    },
    {
      id: Math.random().toString(),
      question: 'Who is the author of the picture "Scream" ?',
      answers: ['Edvard Munch', 'Salvador Dali', 'Michelangelo', 'Luciano Pavarotti'],
      correctAnswer: 1,
    },
    {
      id: Math.random().toString(),
      question: 'Who won the World Cup 2023 ?',
      answers: ['Armenia', 'Argentina', 'Brazil', 'France'],
      correctAnswer: 2,
    },
    {
      id: Math.random().toString(),
      question: 'Who is the best soccer player of all time ?',
      answers: ['Leo Messi', 'Pele', 'Maradonna', 'C. Ronaldo'],
      correctAnswer: 1,
    },
  ]
  constructor() { }

  handleNext(idx: number, corAnsw: number) {
    this.currentIdxSubject.next(this.currentIdxSubject.value + 1);

    if (idx === corAnsw) {
      this.correctCountSubject.next(this.correctCountSubject.value + 1);
    }
  }

  ngOnInit(): void {
    document.documentElement.style.setProperty('--progress-width', this.progressWidth);
  }
}
