import { Component, Input } from "@angular/core";
import { QuizData } from "src/app/models/models";

@Component({
  selector: "app-result",
  templateUrl: "./result.component.html",
  styleUrls: ["./result.component.scss"],
})
export class ResultComponent {
  @Input() props!: { correctCount: number | null; quizItems: QuizData[] | null };
}
<div class="quiz__item quiz__item-two">
  <h2>
    <span>{{ props.correctCount }} </span> correctly answer of
    <span> {{ props.quizItems?.length }} </span> questions
  </h2>
  <a link="/" class="quiz__button">Retry</a>
</div>
